mapper.select:

select 

from product_accessory
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and accessory_name = #{accessoryName,jdbcType=VARCHAR}
  and addtime = #{addTime,jdbcType=INTEGER}

mapper.delete:

delete from product_accessory
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and accessory_name = #{accessoryName,jdbcType=VARCHAR}
  and addtime = #{addTime,jdbcType=INTEGER}

mapper.insert:

insert into product_accessory (produce_date, furnace_num, accessory_name, 
  addtime, class_no, addweight
  )
values (#{produceDate,jdbcType=DATE}, #{furnaceNum,jdbcType=VARCHAR}, #{accessoryName,jdbcType=VARCHAR}, 
  #{addTime,jdbcType=INTEGER}, #{classNo,jdbcType=VARCHAR}, #{addWeight,jdbcType=INTEGER}
  )

mapper.update:

update product_accessory


    class_no = #{classNo,jdbcType=VARCHAR},


    addweight = #{addWeight,jdbcType=INTEGER},


where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and accessory_name = #{accessoryName,jdbcType=VARCHAR}
  and addtime = #{addTime,jdbcType=INTEGER}


update product_accessory
set class_no = #{classNo,jdbcType=VARCHAR},
  addweight = #{addWeight,jdbcType=INTEGER}
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and accessory_name = #{accessoryName,jdbcType=VARCHAR}
  and addtime = #{addTime,jdbcType=INTEGER}
