mapper.select:

select 

from product_alloy
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and alloy_name = #{alloyName,jdbcType=VARCHAR}
  and addtime = #{addtime,jdbcType=INTEGER}

mapper.delete:

delete from product_alloy
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and alloy_name = #{alloyName,jdbcType=VARCHAR}
  and addtime = #{addtime,jdbcType=INTEGER}

mapper.insert:

insert into product_alloy (produce_date, furnace_num, alloy_name, 
  addtime, class_no, addweight
  )
values (#{produceDate,jdbcType=DATE}, #{furnaceNum,jdbcType=VARCHAR}, #{alloyName,jdbcType=VARCHAR}, 
  #{addtime,jdbcType=INTEGER}, #{classNo,jdbcType=VARCHAR}, #{addweight,jdbcType=INTEGER}
  )

mapper.update:

update product_alloy


    class_no = #{classNo,jdbcType=VARCHAR},


    addweight = #{addweight,jdbcType=INTEGER},


where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and alloy_name = #{alloyName,jdbcType=VARCHAR}
  and addtime = #{addtime,jdbcType=INTEGER}


update product_alloy
set class_no = #{classNo,jdbcType=VARCHAR},
  addweight = #{addweight,jdbcType=INTEGER}
where produce_date = #{produceDate,jdbcType=DATE}
  and furnace_num = #{furnaceNum,jdbcType=VARCHAR}
  and alloy_name = #{alloyName,jdbcType=VARCHAR}
  and addtime = #{addtime,jdbcType=INTEGER}
